mapper.select:
select

from simc_resident_social_insurance


    AND resident_name like concat('%', #{residentName}, '%')


    AND resident_phone = #{residentPhone}


    AND resident_sex = #{residentSex}


    AND socialInsurance_type = #{socialInsuranceType}


    AND socialInsurance_state = #{socialInsuranceState}


    AND social_insurance_joint_approval_time >= #{params.beginSocialInsuranceJointApprovalTime}


    AND social_insurance_joint_approval_time <= #{params.endSocialInsuranceJointApprovalTime}


    AND fll_project_name like concat('%', #{fllProjectName}, '%')


order by resident_township_district_id, resident_village_district_id, resident_group_district_id desc
